import React, { useState } from 'react';
import './App.css';

function App() {
  return (
    <div className="container" id="drum-machine">
      <div id="display">
        <div className='row-1'>
          <button className='drum-pad' id='q'>Q</button>
          <button className='drum-pad' id='w'>W</button>
          <button className='drum-pad' id='e'>E</button>
        </div>
        <div className='row-2'>
          <button className='drum-pad' id='a'>A</button>
          <button className='drum-pad' id='s'>S</button>
          <button className='drum-pad' id='d'>D</button>
        </div>
        <div className='row-3'>
          <button className='drum-pad' id='kick-n-hat'>Z</button>
          <button className='drum-pad' id='kick'>X</button>
          <button className='drum-pad' id='closed-hh'>C</button>
        </div>
      </div>
    </div>
  );
}

export default App;
